import { useState, useEffect } from "react";
import useFetch from "./UseFetchcart";
const Main14 = () => {
  const url = "http://localhost:3001/products/";
  const [products] = useFetch(url);
  const [cart, setCart] = useState([]);
  const [qty, setQty] = useState(0);

  useEffect(() => {
    let total = cart.reduce((previousValue, currentValue) => {
      return previousValue + currentValue.quantity;
    }, 0);
    setQty(total);
  }, [cart]);

  const updateCart = (id, q, price) => {
    if (q === 0) {
      const newState1 = cart.filter((item) => {
        return item.id !== id;
      });
      setCart(newState1);
    } else {
      const newState = cart.map((obj) => {
        if (obj.id === id) {
          return { ...obj, quantity: q, total: q * price };
        }
        return obj;
      });

      setCart(newState);
    }
  };

  const addProduct = (obj, id) => {
    const found = cart.find((item) => {
      return item.id === id;
    });

    if (!found) {
      obj.quantity = 1;
      setCart([...cart, obj]);
    }
  };

  return (
    <>
      <div className="row">
        <div className="col-lg-8">
          <h2>Shopping Cart</h2>
        </div>
        <div className="col-lg-4">
          {" "}
          Total:{qty}
          {/* <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal" /> */}
        </div>
      </div>
      <hr></hr>
      <div className="row">
        {products &&
          products.map((obj) => {
            return (
              <div className="col-lg-4" key={obj.id}>
                <div className="card">
                  {/* <img className="card-img-top" src={obj.image}></img> */}
                  <div className="card-body">
                    <h5 className="card-title">{obj.name}</h5>
                    <p className="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                    Price:{obj.price}
                    <button onClick={() => addProduct(obj, obj.id)}>Add</button>
                  </div>
                </div>
              </div>
            );
          })}
        <hr></hr>

        {cart &&
          cart.map((obj) => {
            return (
              <div key={obj.id}>
                {obj.name} - Price:{obj.price} - Total:
                {obj.quantity * obj.price}
                <button
                  onClick={() =>
                    updateCart(obj.id, obj.quantity - 1, obj.price)
                  }
                >
                  -
                </button>
                {obj.quantity}
                <button
                  onClick={() =>
                    updateCart(obj.id, obj.quantity + 1, obj.price)
                  }
                >
                  +
                </button>
              </div>
            );
          })}
      </div>
    </>
  );
};

export default Main14;
